mapper.select:
SELECT COUNT(*)
FROM POINT
WHERE MEM_NO=#{memNo}

SELECT COUNT(*)
FROM POINT
WHERE MEM_NO=#{memNo}

mapper.update:
UPDATE MEMBER
SET MEM_POINT=(SELECT SUM(DECODE(POINT_PART,'적립',1*POINT_PRICE,'사용',-1*POINT_PRICE)) AS PointSum 
FROM POINT
WHERE MEM_NO=#{memNo}) + #{mem_point}
WHERE MEM_NO=#{memNo}

mapper.select:
	SELECT COUNT(*)
	FROM ORD
	WHERE MEM_NO=#{memNo}

	SELECT COUNT(*)
	FROM ADDRESS
	WHERE MEM_NO=#{memNo}

	SELECT *
	FROM ADDRESS
	WHERE MEM_NO=#{memNo}

	SELECT *
	FROM ADDRESS
	WHERE AD_NO=#{mAddress}

mapper.update:
UPDATE ADDRESS
SET AD_TITLE=#{adTitle}, AD_RECEIVER=#{adReceiver}, AD_ADDRESS=#{adAddress},
AD_PHONE=#{adPhone}
WHERE AD_NO=#{adNo}

mapper.select:
	SELECT COUNT(*)
	FROM ORD O
	JOIN PRDT P
	ON (O.PRDT_NO = P.PRDT_NO)
	WHERE MEM_NO=#{memno}
	
	AND ORD_DATE BETWEEN #{start_date} AND #{end_date}
	AND (ORD_STATUS='A' OR ORD_STATUS='B' OR ORD_STATUS='C' OR ORD_STATUS='D' OR ORD_STATUS='E')
	
	
	
	AND ORD_DATE BETWEEN #{start_date} AND #{end_date}
	AND ORD_STATUS=#{ord_status}

SELECT O.*, P.*, (O.ORD_COUNT * P.PRDT_PRICE) AS PRDT_SUMPRICE
FROM ORD O
JOIN PRDT P
ON (O.PRDT_NO = P.PRDT_NO)
WHERE MEM_NO=#{memno}

AND ORD_DATE BETWEEN #{start_date} AND #{end_date} 
AND (ORD_STATUS='A' OR ORD_STATUS='B' OR ORD_STATUS='C' OR ORD_STATUS='D' OR ORD_STATUS='E')


AND ORD_DATE BETWEEN #{start_date} AND #{end_date}
AND ORD_STATUS=#{ord_status}

ORDER BY O.ORD_DATE DESC
