mapper.select:
SELECT COUNT(*)
FROM LIKE_HEART
WHERE RV_NO = #{rvNo} 
AND MEM_NO = #{memNo}

SELECT * 
FROM LIKE_HEART
WHERE RV_NO = #{rvNo} 
AND MEM_NO = #{memNo}

mapper.update:
UPDATE LIKE_HEART
SET LIKE_CHECK = 'Y'
WHERE RV_NO = #{rvNo} 
AND MEM_NO = #{memNo}

UPDATE REVIEW 
SET RV_LIKE =(SELECT RV_LIKE FROM REVIEW WHERE RV_NO=#{rv_no}) +1
WHERE RV_NO = #{rv_no}

UPDATE LIKE_HEART
SET LIKE_CHECK = 'N'
WHERE RV_NO = #{rvNo} 
AND MEM_NO = #{memNo}

UPDATE REVIEW 
SET RV_LIKE =(SELECT RV_LIKE FROM REVIEW WHERE RV_NO=#{rv_no}) -1
WHERE RV_NO = #{rv_no}

UPDATE REVIEW_COMMENT
SET RV_REPORT ='Y'
WHERE RVC_NO = #{rvcNo}
